<template>
  <div class="drawboard-content" :style="{width: width+'px', height: height+'px'}">
    <svg :width="width" :height="height">
      <g></g>
    </svg>

    <div class="drawboard-toolbar">
      <el-tooltip v-for="(control, $index) in controlLayout" :key="$index" class="toolbar-opt" :class="control.type" effect="dark" :content="control.name" placement="top">
        <el-button type="text" @click="handleControl(control)">{{control.name}}</el-button>
      </el-tooltip>
      <!-- <span v-for="(control, $index) in controlLayout" :key="$index" class="toolbar-opt" :class="control.type" @click="handleControl(control)">{{control.name}}</span> -->

      <!-- <span class="toolbar-opt" id="svgPencil" onclick="activePencil()">画笔</span>
      <span class="toolbar-opt active" id="svgDrag" onclick="activeSVGDrag()">拖拽</span>
      <span class="toolbar-opt" id="svgEdit" onclick="editSVG()">编辑</span>
      <span class="toolbar-opt" id="svgRemove" onclick="removeSelector()">删除选中</span>
      <span class="toolbar-opt" id="svgClear" onclick="clearSVG()">清空画布</span> -->
