    </div>
  </div>
</template>

<script>
import * as d3 from 'd3'

export default {
  name: 'Drawboard',
  props: {
    width: { type: Number, default: 0 },
    height: { type: Number, default: 0 },

    // 'pencil', 'drag', 'edit', 'remove', 'clear', 'save'
    controlLayout: {
      type: Array,
      default: () => {
        return [
          { type: 'pencil', name: '画笔' },
          { type: 'drag', name: '拖拽' },
          { type: 'edit', name: '编辑' },
          { type: 'remove', name: '删除' },
          { type: 'clear', name: '清空' },
          { type: 'save', name: '保存' }
        ]
      } },

    bgSrc: { type: String, default: '' }  // 背景图 Base64编码
  },
  data () {
    return {
      // 参数
      svgWidth: this.width,
      svgHeight: this.height,
      controlItems: this.controlLayout,

      draw: false,
      svgDrag: true,
      editStatus: false,

      // line: d3.line().curve(d3.curveBasis),
      // zoom: d3.zoom()
      // .scaleExtent([1, 10])
      // .on('zoom', this.zoomed),
      // drag: d3.drag()
      // // .container(function () { return this; })
      // .subject(this.dragsubject)
      // .on('drag', this.dragged),

      // svg: d3.select('svg'),
      // .call(this.drag)
      // .call(this.zoom).on('dblclick.zoom', null)

      // container: d3.select('svg g'),

      transform: d3.zoomIdentity
      // dragged: null
    }
  },

  computed: {
    line () {
      return d3.line().curve(d3.curveBasis)
    }
    // zoom () {
    //   return d3.zoom().scaleExtent([1, 10])
    // },
    // drag () {
    //   return d3.drag().subject(this.dragsubject).on('drag', this.dragged)
    // }
    // container () {
    //   return this.svg.append('g')
    // }
  },

  methods: {
    initBoard () {
      // let _this = this
      const draggedM = this.dragged()
      d3.select('svg')
        .call(d3.drag()
          .subject(() => { d3.event.subject = [d3.event.x, d3.event.y] })
          .on('drag', (d) => { draggedM(d) }))
        .call(d3.zoom()
          .scaleExtent([1, 10])
          .on('zoom', this.zoomed)).on('dblclick.zoom', null)

      d3.select('svg g')
        .attr('class', 'bg-image')
        .append('image')
        // .attr("xlink:href", "./static/xzd.jpg")
        .attr('xlink:href', 'data:image/jpg;base64,')
        .attr('x', '0')
        .attr('y', '0')
        .attr('width', this.width)
        .attr('height', this.height)
    },
    dragsubject () {
      d3.event.subject = [d3.event.x, d3.event.y]
    },
    dragged (d) {
      if (!this.draw) {
        d3.select('svg g').attr('cx', d.x = d3.event.x).attr('cy', d.y = d3.event.y)
        d3.select('svg g').attr('transform', this.transform)
      } else {
        this.dragged = null
      }
    },
    zoomed () {
      this.transform = d3.event.transform
      // container.attr('transform', transform)
    },

    handleControl (control) {
      switch (control.type) {
        case 'pencil':

          break

        default:
          break
      }
    }
  },
  created () {
    // this.container = d3.select('svg').append('g')
  },
  mounted () {
    // console.log(this.transform)
    this.initBoard()
    // .call(this.drag)
    // .call(this.zoom).on('dblclick.zoom', null)
  }
}
</script>

